import React from 'react';
import "./main.css"

export class Main extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            array : [8, 4, 3, 2, 6, 5],
            desiredSweetness: 6,
            no : []
        }}

    getDesiredSweetness(){
        let desiredSweetness = this.state.desiredSweetness;
        let array = this.state.array;
        let sum = 0
        let fArray = []
        let sArray = []
        for(let i = 0; i < array.length; i++){
            sum+=array[i]
            // console.log("sum",sum)
            if(sum <= desiredSweetness){
                // console.log("i-",i)
                if(i != array.length-1)
                sArray.push(i)
                this.state.no = fArray
                // return this.state.no
                console.log(this.state.no,"no---->")
            }
            if(sum == desiredSweetness){
                sum = 0
                fArray.push(sArray)
                sArray = []
            }
            if(sum > desiredSweetness){
                sum-=array[i]
            }
        }
    }

    componentDidMount(){
        this.getDesiredSweetness()
    }
    
    render()
    {
        return(
            <div className="header">hello aa</div>
        )
    }
}
